
const Fn = require('../utilities/fn-utilities').Fn;

class IOListener {
  constructor() {
    /** <b>IOListener</b> Reception Event
     * @type {Function}     */
    this.ondata;
    /** <b>IOListener</b> Connection Changed Event
     * @type {Function}     */
    this.onchange;
    /** <b>IOListener</b> Connection Error Event
     * @type {Function}     */
    this.onerror;
    /** <b>IOListener</b> Alive Response Event
     * @type {Function}     */
    this.onalive;
    /** <b>IOListener</b> Alive Response Event
     * @type {Function}     */
    this.onalive;
    /** <b>IOListener</b> Application function for Alive Request
     * @type {Function}   */
    this.onAliveRequest;
    /** <b>IOListener</b> Application function for Alive Response
     * @type {Function}   */
    this.onAliveResponse;

    Fn.extends(this, arguments[0]);
    return this;
  }
}

module.exports = {
  IOListener
};
