
const Fn = require('../utilities/fn-utilities').Fn;

/**
 * Topic Device Stream
 * @returns {TopicDevice}
 */
class TopicDevice {
  constructor() {
    /**
     * <b>Topic For Device</b> Topic's name
     * @type {String}
    */
    this.name = "";
    /**
     * <b>Topic For Device</b> Topic's short name
     * @type {String}
    */
    this.nick = "";
    /**
     * <b>Topic For Device</b> Topic's full name
     * @type {String}
    */
    this.fullname = "";
    Fn.extends(this, arguments[0]);
    return this;
  }
}

module.exports = {
  TopicDevice
};
